import{_ as e,c as t,a2 as o,o as r}from"./chunks/framework.3VuPyQdv.js";const i="/docs-demo/wxImages/%E4%BB%80%E4%B9%88%E6%98%AF%E5%AE%BF%E4%B8%BB%E7%8E%AF%E5%A2%83.png",p="/docs-demo/wxImages/%E5%B0%8F%E7%A8%8B%E5%BA%8F%E7%9A%84%E5%AE%BF%E4%B8%BB%E7%8E%AF%E5%A2%83.png",E=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"doc-text/wx/wx05.md","filePath":"doc-text/wx/wx05.md","lastUpdated":1751696524000}'),n={name:"doc-text/wx/wx05.md"};function s(d,a,c,l,h,m){return r(),t("div",null,a[0]||(a[0]=[o('<h3 id="_4-小程序的宿主环境" tabindex="-1">4.小程序的宿主环境 <a class="header-anchor" href="#_4-小程序的宿主环境" aria-label="Permalink to &quot;4.小程序的宿主环境&quot;">​</a></h3><h3 id="_4-1-什么是宿主环境" tabindex="-1">4.1 什么是宿主环境 <a class="header-anchor" href="#_4-1-什么是宿主环境" aria-label="Permalink to &quot;4.1  什么是宿主环境&quot;">​</a></h3><p>​ 宿主环境（host environment）指的是程序运行所必须的依赖环境。例如：</p><p>Android 系统和 iOS 系统是两个不同的宿主环境。安卓版的微信 App 是不能在 iOS 环境下运行的，所以，Android 是安卓软件的宿主环境，脱离了宿主环境的软件是没有任何意义的！</p><p><img src="'+i+'" alt="image-20230301215436684"></p><hr><h3 id="_4-2-小程序的宿主环境" tabindex="-1">4.2 小程序的宿主环境 <a class="header-anchor" href="#_4-2-小程序的宿主环境" aria-label="Permalink to &quot;4.2 小程序的宿主环境&quot;">​</a></h3><p>​ 手机微信是小程序的宿主环境，如图所示：</p><p>​ <img src="'+p+'" alt="image-20230301215535300"></p><p>小程序借助宿主环境提供的能力，可以完成许多普通网页无法完成的功能，例如：微信扫码、微信支付、微信登录、地理定位、etc…</p><hr><h3 id="_4-3-小程序宿主环境包含的内容" tabindex="-1">4.3 小程序宿主环境包含的内容 <a class="header-anchor" href="#_4-3-小程序宿主环境包含的内容" aria-label="Permalink to &quot;4.3 小程序宿主环境包含的内容&quot;">​</a></h3><p>​ ①通信模型</p><p>​ ②运行机制</p><p>​ ③组件</p><p>​ ④API</p>',16)]))}const x=e(n,[["render",s]]);export{E as __pageData,x as default};
