import{_ as e,c as a,a2 as d,o as r}from"./chunks/framework.3VuPyQdv.js";const f=JSON.parse('{"title":"20. 页面配置","description":"","frontmatter":{},"headers":[],"relativePath":"doc-text/wx/wx21.md","filePath":"doc-text/wx/wx21.md","lastUpdated":1743089594000}'),l={name:"doc-text/wx/wx21.md"};function o(n,t,i,s,h,x){return r(),a("div",null,t[0]||(t[0]=[d('<h1 id="_20-页面配置" tabindex="-1">20. 页面配置 <a class="header-anchor" href="#_20-页面配置" aria-label="Permalink to &quot;20. 页面配置&quot;">​</a></h1><h4 id="_20-1-页面配置文件的作用" tabindex="-1">20.1 页面配置文件的作用 <a class="header-anchor" href="#_20-1-页面配置文件的作用" aria-label="Permalink to &quot;20.1 页面配置文件的作用&quot;">​</a></h4><p>​ 小程序中，每个页面都有自己的 .json 配置文件，用来对当前页面的窗口外观、页面效果等进行配置。</p><hr><h4 id="_20-2-页面配置和全局配置的关系" tabindex="-1">20.2 页面配置和全局配置的关系 <a class="header-anchor" href="#_20-2-页面配置和全局配置的关系" aria-label="Permalink to &quot;20.2 页面配置和全局配置的关系&quot;">​</a></h4><p>​ 小程序中，app.json 中的 window 节点，可以全局配置小程序中每个页面的窗口表现。</p><p>​ 如果某些小程序页面想要拥有特殊的窗口表现，此时，“页面级别的 .json 配置文件”就可以实现这种需求。</p><p>​ 注意：当页面配置与全局配置冲突时，根据就近原则，最终的效果以页面配置为准。</p><hr><h4 id="_20-3页面配置中常用的配置项" tabindex="-1">20.3页面配置中常用的配置项 <a class="header-anchor" href="#_20-3页面配置中常用的配置项" aria-label="Permalink to &quot;20.3页面配置中常用的配置项&quot;">​</a></h4><table tabindex="0"><thead><tr><th>属性</th><th>类型</th><th>默认值</th><th style="text-align:left;">描述</th></tr></thead><tbody><tr><td>navigationBarBackgroundColor</td><td>HexColor</td><td>#000000</td><td style="text-align:left;">当前页面导航栏背景颜色，如 #000000</td></tr><tr><td>navigationBarTextStyle</td><td>String</td><td>white</td><td style="text-align:left;">当前页面导航栏标题颜色，仅支持 black / white</td></tr><tr><td>navigationBarTitleText</td><td>String</td><td></td><td style="text-align:left;">当前页面导航栏标题文字内容</td></tr><tr><td>backgroundColor</td><td>HexColor</td><td>#ffffff</td><td style="text-align:left;">当前页面窗口的背景色</td></tr><tr><td>backgroundTextStyle</td><td>String</td><td>dark</td><td style="text-align:left;">当前页面下拉 loading 的样式，仅支持 dark / light</td></tr><tr><td>enablePullDownRefresh</td><td>Boolean</td><td>false</td><td style="text-align:left;">是否为当前页面开启下拉刷新的效果</td></tr><tr><td>onReachBottomDistance</td><td>Number</td><td>50</td><td style="text-align:left;">页面上拉触底事件触发时距页面底部距离，单位为 px</td></tr></tbody></table>',11)]))}const p=e(l,[["render",o]]);export{f as __pageData,p as default};
